// Helper functions for mapping
func mapEntities(owners []*ent.Entity, ctx context.Context) []Entity {
	entities := []Entity{}
	taxInformations := []TaxInformation{}
	for _, o := range owners {

		// Get Tax information
		txs, _ := o.QueryEntityTaxInformation().All(ctx)
		for _, t := range txs {
			tx := TaxInformation{
				TaxId: t.TaxId,
				Type:  string(t.Type),
			}
			taxInformations = append(taxInformations, tx)
		}

		e := Entity{
			Id:          o.ID.String(),
			Name:        o.Fullname,
			Active:      o.Active,
			DateCreated: isValidBankDate(o.DateCreated.Format(util.APIDateFormat)),
			SecurityInformation: SecurityInformation{
				LastLoginDate: isValidBankDate(o.LastLoginDate.Format(util.APIDateFormat)),
				Username:      o.Username,
				Token:         o.Token,
			},
			URI: FiniteUri{
				URL: o.URL,
			},
			Type:           string(o.Type),
			TaxInformation: taxInformations,
		}
		entities = append(entities, e)
	}
	return entities
}

func mapAddresses(addrs []*ent.EntityAddress) []Address {
	addresses := []Address{}
	for _, addr := range addrs {
		a := Address{
			Country:    addr.Country,
			City:       addr.City,
			PostalCode: addr.PostalCode,
			State:      addr.State,
			Type:       string(addr.Type),
			Line1:      addr.Line1,
			Line2:      addr.Line2,
			Line3:      addr.Line3,
			Primary:    addr.Primary,
		}
		addresses = append(addresses, a)
	}
	return addresses
}

func mapContactPoints(cntpts []*ent.EntityContactPoint) []ContactPoint {
	contactpoints := []ContactPoint{}
	for _, cntpt := range cntpts {
		c := ContactPoint{
			Prefix: strconv.Itoa(cntpt.Prefix),
			Name:   cntpt.Name,
			Type:   cntpt.Type.String(),
			Suffix: strconv.Itoa(cntpt.Suffix),
			Value:  cntpt.Value,
		}
		contactpoints = append(contactpoints, c)
	}
	return contactpoints
}

func mapTaxInformations(taxinfs []*ent.EntityTaxInformation) []TaxInformation {
	taxinformations := []TaxInformation{}
	for _, taxinf := range taxinfs {
		c := TaxInformation{
			TaxId: taxinf.TaxId,
			Type:  string(taxinf.Type),
		}
		taxinformations = append(taxinformations, c)
	}
	return taxinformations
}
